<template>
  <div id="el_loading" v-show="isShow">
      <div :class="maskClass"></div>
      <div class="ui-toast">
        <i class="ui-loading ui-icon_toast"></i>
        <p class="ui-toast__content">{{message}}</p>
      </div>
  </div>
</template>

<script>
export default {
  name: "loading",
  data() {
    return {
      isShow: false,
      maskType: 1,
      message: "",
      callback: function() {}
    };
  },
  methods: {
    notify: function() {
      this.callback();
    }
  },
  computed: {
    maskClass: function() {
      let maskClass = "ui-mask_transparent";
      switch (this.maskType) {
        case 1:
          maskClass = "ui-mask_transparent";
          break;
        case 2:
          maskClass = "ui-mask_bg";
          break;
      }
      return maskClass;
    }
  }
};
</script>

<style>
.ui-mask {
  position: fixed;
  z-index: 1000;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.6);
}

.ui-mask_transparent {
  position: fixed;
  z-index: 1000;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
}

.ui-mask_bg {
  position: fixed;
  z-index: 1000;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  background: #fafafa;
}

.ui-toast {
  position: fixed;
  z-index: 5000;
  width: 100px;
  min-height: 100px;
  top: 180px;
  left: 50%;
  margin-left: -50px;
  background: rgba(17, 17, 17, 0.7);
  text-align: center;
  border-radius: 5px;
  color: #ffffff;
}
.ui-icon_toast {
  margin: 22px 0 0;
  display: block;
}

.ui-icon_toast .ui-icon-success-no-circle :before {
  color: #ffffff;
  font-size: 55px;
}
.ui-icon_toast .ui-loading {
  margin: 30px 0 0;
  width: 38px;
  height: 38px;
  vertical-align: baseline;
}

.ui-loading {
  width: 30px;
  height: 30px;
  display: inline-block;
  vertical-align: middle;
  animation: uiLoading 1s steps(12, end) infinite;
  background: transparent
    url(data:image/svg+xml;base64,)
    no-repeat;
  background-size: 100%;
}

.ui-loading .ui-loading_transparent,
  .ui-btn_loading.ui-btn_primary,
  .ui-btn_loading.ui-btn_warn{
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='120' height='120' viewBox='0 0 100 100'%3E%3Cpath fill='none' d='M0 0h100v100H0z'/%3E%3Crect xmlns='http://www.w3.org/2000/svg' width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.56)' rx='5' ry='5' transform='translate(0 -30)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.5)' rx='5' ry='5' transform='rotate(30 105.98 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.43)' rx='5' ry='5' transform='rotate(60 75.98 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.38)' rx='5' ry='5' transform='rotate(90 65 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.32)' rx='5' ry='5' transform='rotate(120 58.66 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.28)' rx='5' ry='5' transform='rotate(150 54.02 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.25)' rx='5' ry='5' transform='rotate(180 50 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.2)' rx='5' ry='5' transform='rotate(-150 45.98 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.17)' rx='5' ry='5' transform='rotate(-120 41.34 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.14)' rx='5' ry='5' transform='rotate(-90 35 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.1)' rx='5' ry='5' transform='rotate(-60 24.02 65)'/%3E%3Crect width='7' height='20' x='46.5' y='40' fill='rgba(255,255,255,.03)' rx='5' ry='5' transform='rotate(-30 -5.98 65)'/%3E%3C/svg%3E");
  }
@-webkit-keyframes uiLoading {
  0% {
    transform: rotate3d(0, 0, 1, 0deg);
  }

  100% {
    transform: rotate3d(0, 0, 1, 360deg);
  }
}

@keyframes uiLoading {
  0% {
    transform: rotate3d(0, 0, 1, 0deg);
  }

  100% {
    transform: rotate3d(0, 0, 1, 360deg);
  }
}

.ui-toast__content {
  margin-top: 5px;
}
</style>
